CREATE OR REPLACE FUNCTION get_personal_title ( nr_sequence_p bigint, ie_option_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);


BEGIN

	select CASE WHEN ie_option_p='A' THEN ds_sigla WHEN ie_option_p='T' THEN ds_forma_tratamento END
	into STRICT	ds_retorno_w
	from	pf_forma_tratamento
	where	nr_sequencia = nr_sequence_p
	and ie_situacao = 'A';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
