CREATE OR REPLACE FUNCTION get_person_aut_insurance (nr_seq_autorizacao_p bigint) RETURNS varchar AS $body$
DECLARE


return_w					pessoa_fisica.cd_pessoa_fisica%type 				:= null;
cd_pessoa_fisica_w			autorizacao_convenio.cd_pessoa_fisica%type 			:= null;
nr_atendimento_w			autorizacao_convenio.nr_atendimento%type 			:= null;
nr_seq_agenda_w				autorizacao_convenio.nr_seq_agenda%type 			:= null;
nr_seq_agenda_consulta_w	autorizacao_convenio.nr_seq_agenda_consulta%type	:= null;
nr_seq_paciente_setor_w		autorizacao_convenio.nr_seq_paciente_setor%type 	:= null;
nr_seq_gestao_w				autorizacao_convenio.nr_seq_gestao%type 			:= null;

c01 CURSOR FOR
SELECT	cd_pessoa_fisica,
		nr_atendimento,
		nr_seq_agenda,
		nr_seq_agenda_consulta,
		nr_seq_paciente_setor,
		nr_seq_gestao
from	autorizacao_convenio
where	nr_sequencia = nr_seq_autorizacao_p;


BEGIN

open c01;
	loop
	fetch c01 into	cd_pessoa_fisica_w,
					nr_atendimento_w,
					nr_seq_agenda_w,
					nr_seq_agenda_consulta_w,
					nr_seq_paciente_setor_w,
					nr_seq_gestao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
				return_w := cd_pessoa_fisica_w;
				exit;
			elsif (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
				select 	obter_pessoa_atendimento(nr_atendimento_w, 'C')
				into STRICT	return_w
				;
				exit;
			elsif (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then
				select 	max(cd_pessoa_fisica)
				into STRICT	return_w
				from 	agenda_paciente
				where 	nr_sequencia = nr_seq_agenda_w;
				exit;
			elsif (nr_seq_agenda_consulta_w IS NOT NULL AND nr_seq_agenda_consulta_w::text <> '') then
				select 	max(cd_pessoa_fisica)
				into STRICT	return_w
				from 	agenda_paciente
				where 	nr_sequencia = nr_seq_agenda_consulta_w;
				exit;
			elsif (nr_seq_paciente_setor_w IS NOT NULL AND nr_seq_paciente_setor_w::text <> '') then
				select 	obter_paciente_autor_onc(nr_seq_paciente_setor_w)
				into STRICT 	return_w
				;
				exit;
			elsif (nr_seq_gestao_w IS NOT NULL AND nr_seq_gestao_w::text <> '') then
				select 	obter_paciente_gestao_vagas(nr_seq_gestao_w,'C')
				into STRICT 	return_w
				;
				exit;
			end if;

		exception
		when others then
			return_w := null;
		end;

	end loop;
	close c01;

return return_w;
end;
$body$
