CREATE OR REPLACE FUNCTION get_person_encounter (cd_pessoa_fisica_p text, cd_estabelecimento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255) := '';								
nr_atendimento_w	bigint;

/*
ie_opcao_p
	TI	-	Type of Insurance
*/
	

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin
	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	atendimento_paciente
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	coalesce(dt_alta::text, '') = ''
	and	ie_fim_conta <> 'F';
	end;
end if;

if (ie_opcao_p = 'TI' and (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '')) then
	select	ie_tipo_convenio
	into STRICT	ds_retorno_w
	from	atendimento_paciente
	where	nr_atendimento	= nr_atendimento_w;

end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
