CREATE OR REPLACE FUNCTION get_person_name_estab ( cd_physical_person_p text, nm_physical_person_p text, cd_establishment_p estabelecimento.cd_estabelecimento%type default null) RETURNS varchar AS $body$
DECLARE


ds_return_w		varchar(60);
nr_seq_person_name_w		pessoa_fisica.nr_seq_person_name%type;


BEGIN
if (cd_physical_person_p IS NOT NULL AND cd_physical_person_p::text <> '') then
	begin

	select	max(nr_seq_person_name)
	into STRICT	nr_seq_person_name_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_physical_person_p;

	select	substr(nm_pessoa_fisica,1,60)
	into STRICT	ds_return_w
	from 	table(search_names_dev(null, cd_physical_person_p, nr_seq_person_name_w, null, null, cd_establishment_p));

	end;
else
	return nm_physical_person_p;
end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
