CREATE OR REPLACE FUNCTION get_post_natal_obstertrics ( ds_col_name_p text, nr_atendimento_p bigint, nr_row_order_p bigint DEFAULT 1) RETURNS varchar AS $body$
DECLARE

    sql_w           varchar(500) := ' ';
    retorno_w       varchar(100) := ' ';
    query_order_w   varchar(5) := '';

BEGIN
    SELECT
        CASE WHEN nr_row_order_p=1 THEN  'DESC'  ELSE 'ASC' END
    INTO STRICT query_order_w
;

    sql_w := 'SELECT * FROM (SELECT to_char('
            || ds_col_name_p
            || ') FROM POST_NATAL_OBSTERTRICS WHERE nr_atendimento = :nr_atendimento_p ORDER BY DT_AVALIACAO '
            || query_order_w
            || ') WHERE ROWNUM = 1';

    EXECUTE sql_w
    INTO STRICT retorno_w
        USING nr_atendimento_p;
    RETURN retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
