CREATE OR REPLACE FUNCTION get_previous_score ( cd_formulario_aval_p nursing_care_encounter.nr_seq_nursing_care%TYPE, dt_avaliacao_p nursing_care_encounter.dt_avaliacao%TYPE, nr_atendimento_p nursing_care_encounter.nr_atendimento%TYPE, field_score_type_p bigint ) RETURNS varchar AS $body$
DECLARE

    qt_pontuacao_w nursing_care_encounter.qt_pontuacao_a%TYPE;

BEGIN
    SELECT
        coalesce(qt_pontuacao_a, qt_pontuacao_b, qt_pontuacao_c)
    INTO STRICT qt_pontuacao_w
    FROM (
            SELECT
                CASE field_score_type_p
                    WHEN 1 THEN
                        qt_pontuacao_a
                END AS qt_pontuacao_a,
                CASE field_score_type_p
                    WHEN 2 THEN
                        qt_pontuacao_b
                END AS qt_pontuacao_b,
                CASE field_score_type_p
                    WHEN 3 THEN
                        qt_pontuacao_c
                END AS qt_pontuacao_c
            FROM (
                    SELECT
                        t.*
                    FROM (
                            SELECT
                                1 nce,
                                dt_avaliacao,
                                qt_pontuacao_a,
                                qt_pontuacao_b,
                                qt_pontuacao_c,
                                ROW_NUMBER() OVER (
                                    ORDER BY
                                        dt_avaliacao DESC
                                ) rn
                            FROM
                                nursing_care_encounter
                            WHERE
                                nr_atendimento = nr_atendimento_p
                                AND ie_situacao = 'A'
                                AND pkg_date_utils.start_of(dt_avaliacao, 'dd') = pkg_date_utils.start_of(dt_avaliacao_p, 'dd')
                                AND nr_seq_nursing_care = coalesce(cd_formulario_aval_p, nr_seq_nursing_care)
                            ORDER BY
                                dt_avaliacao DESC
                        ) t
                    WHERE
                        rn = 2

UNION

                    SELECT
                        t.*
                    FROM (
                            SELECT
                                2 nce,
                                dt_avaliacao,
                                qt_pontuacao_a,
                                qt_pontuacao_b,
                                qt_pontuacao_c,
                                ROW_NUMBER() OVER (
                                    ORDER BY
                                        dt_avaliacao DESC
                                ) rn
                            FROM
                                nursing_care_encounter
                            WHERE
                                nr_atendimento = nr_atendimento_p
                                AND ie_situacao = 'A'
                                AND pkg_date_utils.start_of(dt_avaliacao, 'dd') < pkg_date_utils.start_of(dt_avaliacao_p, 'dd')
                                AND nr_seq_nursing_care = coalesce(cd_formulario_aval_p, nr_seq_nursing_care)
                            ORDER BY
                                dt_avaliacao DESC
                        ) t
                    WHERE
                        rn = 1
                ) alias13 LIMIT 1) alias14;

    RETURN qt_pontuacao_w;
END;
