SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_prioridade_diag ( NM_TABELA_P text, NR_SEQUENCIA_P pe_prescr_diag.NR_SEQUENCIA%TYPE ) RETURNS varchar AS $body$
DECLARE

    NR_PRIORIDADE_W varchar(5) := null;

BEGIN

    IF (NM_TABELA_P = 'PE_PRESCR_DIAG') THEN

        SELECT MAX(NR_PRIORIDADE) INTO STRICT NR_PRIORIDADE_W
        FROM pe_prescr_diag WHERE NR_SEQUENCIA = NR_SEQUENCIA_P;

    ELSIF (NM_TABELA_P = 'PATIENT_CP_GOAL') THEN
    
        SELECT MAX(a.NR_PRIORIDADE) INTO STRICT NR_PRIORIDADE_W
        FROM pe_prescr_diag a 
        INNER JOIN PATIENT_CP_GOAL b ON
            b.nr_seq_prescr_diag = a.NR_SEQUENCIA
        WHERE b.NR_SEQUENCIA = NR_SEQUENCIA_P;

    ELSIF (NM_TABELA_P = 'PATIENT_CP_INDICATOR') THEN
    
        SELECT MAX(a.NR_PRIORIDADE) INTO STRICT NR_PRIORIDADE_W
        FROM pe_prescr_diag a 
        INNER JOIN PATIENT_CP_GOAL b ON
            b.nr_seq_prescr_diag = a.NR_SEQUENCIA
        INNER JOIN PATIENT_CP_INDICATOR c ON
            c.nr_seq_pat_cp_goal = b.NR_SEQUENCIA
        WHERE c.NR_SEQUENCIA = NR_SEQUENCIA_P;

    ELSIF (NM_TABELA_P = 'PATIENT_CP_INTERV_PLAN') THEN
    
        SELECT MAX(a.NR_PRIORIDADE) INTO STRICT NR_PRIORIDADE_W
        FROM pe_prescr_diag a 
        INNER JOIN PATIENT_CP_INTERV_PLAN b ON
            b.nr_seq_prescr_diag = a.NR_SEQUENCIA
        WHERE b.NR_SEQUENCIA = NR_SEQUENCIA_P;

    ELSIF (NM_TABELA_P = 'PATIENT_CP_INTERVENTION') THEN
    
        SELECT MAX(a.NR_PRIORIDADE) INTO STRICT NR_PRIORIDADE_W
        FROM pe_prescr_diag a 
        INNER JOIN PATIENT_CP_INTERV_PLAN b ON
            b.nr_seq_prescr_diag = a.NR_SEQUENCIA
        INNER JOIN PATIENT_CP_INTERVENTION c ON
            c.nr_seq_pat_cp_interv_plan = b.NR_SEQUENCIA
        WHERE c.NR_SEQUENCIA = NR_SEQUENCIA_P;

    ELSIF (NM_TABELA_P = 'PE_PRESCR_PROC') THEN
    
        SELECT MAX(a.NR_PRIORIDADE) INTO STRICT NR_PRIORIDADE_W
        FROM pe_prescr_diag a 
        INNER JOIN PE_PRESCR_PROC b ON
            b.ie_related_problem = a.NR_SEQUENCIA
        WHERE b.NR_SEQUENCIA = NR_SEQUENCIA_P;

    
    END IF;

    RETURN NR_PRIORIDADE_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
