CREATE OR REPLACE FUNCTION get_privacy_rule_establishment (nr_sequencia_p bigint, ie_type_p text) RETURNS varchar AS $body$
DECLARE


ds_rule_w	privacy_rule.ds_rule%type := '';


BEGIN

	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '' AND ie_type_p IS NOT NULL AND ie_type_p::text <> '') then

		select  ds_rule
		into STRICT 	ds_rule_w
		from 	privacy_rule
		where   nr_sequencia = nr_sequencia_p
		and (ie_type = 'A' or ie_type = ie_type_p);

	end if;

	return ds_rule_w;

end;
$body$
