CREATE OR REPLACE FUNCTION get_private_days_count ( nr_atendimento_p bigint, cd_convenio_p bigint, cd_categoria_p text) RETURNS bigint AS $body$
DECLARE

/*
Get patient total days stayed in the private accommodation
*/
 cd_setor_atendimento_w	atend_paciente_unidade.cd_setor_atendimento%type;
 cd_unidade_basica_w	atend_paciente_unidade.cd_unidade_basica%type;
 cd_unidade_compl_w		atend_paciente_unidade.cd_unidade_compl%type;
 dt_entrada_unidade_w	atend_paciente_unidade.dt_entrada_unidade%type;
 dt_saida_unidade_w		atend_paciente_unidade.dt_saida_unidade%type;
 cd_tipo_acomodacao_w	atend_paciente_unidade.cd_tipo_acomodacao%type;
 qt_priv_acc_w			bigint;
 qt_retorno				bigint :=0;		
			
			
	C01 CURSOR FOR
	SELECT	a.cd_setor_atendimento,
			a.cd_unidade_basica,
			a.cd_unidade_compl, 
			a.dt_entrada_unidade, 
			a.dt_saida_unidade,
			a.cd_tipo_acomodacao
	from atend_paciente_unidade a, atend_categoria_convenio b
	where a.nr_atendimento=nr_atendimento_p
	and a.nr_atendimento=b.nr_atendimento
	and a.dt_entrada_unidade between b.dt_inicio_vigencia and coalesce(b.dt_final_vigencia,clock_timestamp())
	and b.cd_convenio = cd_convenio_p
	and coalesce(b.cd_categoria,0) = coalesce(cd_categoria_p,0)
	order by nr_sequencia;
			
	c02 CURSOR FOR
	SELECT count(*)
	from unidade_atendimento a, tipo_acomodacao b, drg_billing_configuration c
	where a.cd_setor_atendimento = cd_setor_atendimento_w
	and a.cd_unidade_basica= cd_unidade_basica_w
	and coalesce(trim(both a.cd_unidade_compl),0) = coalesce(trim(both cd_unidade_compl_w),0)
	and a.cd_tipo_acomodacao = cd_tipo_acomodacao_w
	and a.cd_tipo_acomodacao = b.cd_tipo_acomodacao
	and b.cd_tipo_acomodacao = c.cd_drg_priv_acc
	and c.cd_convenio=cd_convenio_p;

			

BEGIN

	open c01;
	loop
	fetch c01 into	
			cd_setor_atendimento_w,
			cd_unidade_basica_w,
			cd_unidade_compl_w,
			dt_entrada_unidade_w,
			dt_saida_unidade_w,
			cd_tipo_acomodacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	
		begin
		open c02;
		loop
		fetch c02 into qt_priv_acc_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
				if (qt_priv_acc_w >0)then
				  qt_retorno := qt_retorno + ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(coalesce(dt_saida_unidade_w,clock_timestamp()) + (1/24*60*60)) - ESTABLISHMENT_TIMEZONE_UTILS.startOfDay((dt_entrada_unidade_w) + (1/24*60*60));
				end if;
			end;
		end loop;
		close c02;
		end;
	end loop;
	close c01;
return qt_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
