CREATE OR REPLACE FUNCTION get_procedure_message (nr_seq_procedure_p proc_interno.nr_sequencia%type) RETURNS RULE_PROCEDURE_MESSAGE.DS_MENSAGEM%TYPE AS $body$
DECLARE

ds_mensagem_w rule_procedure_message.ds_mensagem%type;


BEGIN

select  max(b.ds_mensagem)
into STRICT  ds_mensagem_w
from  proc_interno a
inner join  rule_procedure_message b on b.nr_sequencia = a.nr_seq_message_proc
where   a.nr_sequencia = nr_seq_procedure_p
and coalesce(b.ie_situacao, 'N') = 'A';

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
