CREATE OR REPLACE FUNCTION get_provider_details ( cd_medico_p text, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_provider_w varchar(8);


BEGIN

select  max(coalesce(nr_provider, null)) 
into STRICT    nr_provider_w
from    medical_provider_number a
where   a.cd_medico = cd_medico_p
and (a.cd_estabelecimento = cd_estabelecimento_p or coalesce(a.cd_estabelecimento::text, '') = '')
and (a.cd_department = cd_setor_atendimento_p or coalesce(a.cd_department::text, '') = '');

return nr_provider_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
