CREATE OR REPLACE FUNCTION get_pzn_code ( cd_material_p material_sistema_externo.cd_material%type, ie_sistema_p material_sistema_externo.ie_sistema%type) RETURNS MATERIAL_SISTEMA_EXTERNO.CD_CODIGO%TYPE AS $body$
DECLARE


cd_codigo_w	material_sistema_externo.cd_codigo%type;


BEGIN

begin
select	cd_codigo
into STRICT	cd_codigo_w
from	material_sistema_externo
where	cd_material = cd_material_p
and	ie_sistema = ie_sistema_p  LIMIT 1;
exception
