CREATE OR REPLACE FUNCTION get_race (cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%TYPE) RETURNS COR_PELE.IE_NEGRO%TYPE AS $body$
DECLARE

  cd_pessoa_fisica_w pessoa_fisica.cd_pessoa_fisica%TYPE := cd_pessoa_fisica_p;
  black_race_w cor_pele.ie_negro%TYPE;

BEGIN
  select cp.ie_negro
    into STRICT black_race_w
    from cor_pele cp
    left join pessoa_fisica pf
      on pf.NR_SEQ_COR_PELE = cp.nr_sequencia
   where pf.cd_pessoa_fisica = cd_pessoa_fisica_w;
  return black_race_w;
end;
