CREATE OR REPLACE FUNCTION get_regra_liberar_isol (cd_perfil_p bigint, nm_usuario_p text, nr_seq_precaucao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w	varchar(2);


BEGIN
ds_return_w := 'N';

select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ds_return_w
from 	cih_precaucao a,
	cih_precaucao_lib_isol b
where 	a.nr_sequencia = b.nr_seq_precaucao
and	a.nr_sequencia = nr_seq_precaucao_p
and 	a.ie_situacao = 'A';

if (ds_return_w = 'N') then
	begin

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_return_w
	from 	cih_precaucao a,
		cih_precaucao_lib_isol b
	where 	a.nr_sequencia = b.nr_seq_precaucao
	and	a.nr_sequencia = nr_seq_precaucao_p
	and 	coalesce(a.ie_situacao,'A') = 'A'
	and (b.cd_perfil = cd_perfil_p or b.nm_usuario_lib = nm_usuario_p);


	end;
end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
