SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_result_kpi_sla (kpi_type_p text, dt_initial_p text, dt_final_p text ) RETURNS bigint AS $body$
DECLARE


qt_kpi_value_w			double precision;
qt_response_breached_w		bigint;
qt_resolution_breached_w	bigint;
qt_total_w			double precision;
qt_sla_breached_w		bigint;


BEGIN
	qt_kpi_value_w := 100;
	qt_response_breached_w := 0;
	qt_resolution_breached_w := 0;
	qt_total_w := 0;

	if (kpi_type_p = 'MSWP') then -- Meet SLA with Penalty
	begin
		select (count(status_sla_response) * 2)
		into STRICT	qt_total_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and	sla_detail in ('CSFP', 'COSP');
	
		select  count(status_sla_response)
		into STRICT	qt_response_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_response) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('CSFP', 'COSP')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
	
		select  count(status_sla_resolution)
		into STRICT	qt_resolution_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_resolution) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('CSFP', 'COSP')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
		
	end;
	elsif (kpi_type_p = 'MSWC') then --  Meet SLA with Contract
	begin
		select (count(status_sla_response) * 2)
		into STRICT	qt_total_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and	sla_detail in ('CSFNP', 'COSNP', 'CSFP', 'COSP');
	
		select  count(status_sla_response)
		into STRICT	qt_response_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_response) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('CSFNP', 'COSNP', 'CSFP', 'COSP')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
	
		select  count(status_sla_resolution)
		into STRICT	qt_resolution_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_resolution) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('CSFNP', 'COSNP', 'CSFP', 'COSP')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
		
	end;
	elsif (kpi_type_p = 'PSFNP') then -- Meet SLA no contract
	begin
		select (count(status_sla_response) * 2)
		into STRICT	qt_total_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and	sla_detail in ('PSFNP');
	
		select  count(status_sla_response)
		into STRICT	qt_response_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_response) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('PSFNP')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
	
		select  count(status_sla_resolution)
		into STRICT	qt_resolution_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_resolution) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('PSFNP')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
		
	end;
	elsif (kpi_type_p = 'PSOTR') then -- Meet All others defects(Philips and Customer)
	begin
		select (count(status_sla_response) * 2)
		into STRICT	qt_total_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and	sla_detail in ('PSOTR');
	
		select  count(status_sla_response)
		into STRICT	qt_response_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_response) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('PSOTR')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
	
		select  count(status_sla_resolution)
		into STRICT	qt_resolution_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_resolution) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('PSOTR')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
		
	end;
	elsif (kpi_type_p = 'MTSLA') then -- Meet total SLA
	begin
		select (count(status_sla_response) * 2)
		into STRICT	qt_total_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and	sla_detail in ('CSFNP', 'COSNP', 'CSFP', 'COSP', 'PSFNP', 'PSOTR');
	
		select  count(status_sla_response)
		into STRICT	qt_response_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_response) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('CSFNP', 'COSNP', 'CSFP', 'COSP', 'PSFNP', 'PSOTR')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
	
		select  count(status_sla_resolution)
		into STRICT	qt_resolution_breached_w
		from    sla_informations
		where   extract_date between to_date(dt_initial_p) and fim_dia(to_date(dt_final_p))
		and     upper(status_sla_resolution) = upper('breached')
		and     upper(philips_classification) = upper('defeito')
		and	sla_detail in ('CSFNP', 'COSNP', 'CSFP', 'COSP', 'PSFNP', 'PSOTR')
		and	so_sla = 'S'
		and	develop_management_id <> 68;
		
	end;
	end if;

	qt_total_w := qt_total_w * 2; -- uma vez que, contamos 2 slas por ordem de servi√ßo.
	qt_sla_breached_w := qt_response_breached_w + qt_resolution_breached_w;

	if	(((qt_response_breached_w <> 0) or (qt_resolution_breached_w <> 0)) and (qt_total_w <> 0)) then
		qt_kpi_value_w := 100 - ((qt_sla_breached_w * 100) / qt_total_w);
	end if;


return	qt_kpi_value_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
