CREATE OR REPLACE FUNCTION get_rp_desc_jpn (nr_seq_rp bigint) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w    varchar(255);

BEGIN

if (nr_seq_rp IS NOT NULL AND nr_seq_rp::text <> '') then
	begin
		SELECT	'RP '||NR_SEQUENCIA ||' Route '||DS_ROUTE||' INTERVAL '||CD_INTERVAL
		into STRICT	ds_retorno_w
		FROM   RP_MEDICINE
		where	NR_SEQUENCIA = nr_seq_rp;
	exception
		when others then
		ds_retorno_w := '';
	end;
else
		ds_retorno_w := '';
end if;
return		ds_retorno_w;
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
