SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_salutation ( ie_estado_civil_p bigint, ie_sexo_p text) RETURNS varchar AS $body$
DECLARE


ds_valor_salutation_w varchar(255) := null;

/* ie_estado_civil
1 - single
2 - married
3 - divorced
7 - concubinage/civil union
9 - others
*/
BEGIN
if (ie_estado_civil_p IS NOT NULL AND ie_estado_civil_p::text <> '') and (ie_sexo_p IS NOT NULL AND ie_sexo_p::text <> '') then

	if (ie_estado_civil_p in (1, 3, 7, 9) and (ie_sexo_p = 'F')) then
		ds_valor_salutation_w := 'Ms';
	elsif (ie_estado_civil_p    = 2 and ie_sexo_p = 'F') then
		ds_valor_salutation_w := 'Mrs';
	elsif (ie_sexo_p = 'M') then
		ds_valor_salutation_w := 'Mr';
	end if;

end if;

return ds_valor_salutation_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
