CREATE OR REPLACE FUNCTION get_score_body_systems_suep ( cd_sequencia_algorithm_p bigint, ie_algorithm_type_p text, ie_sistema_organismo_p text) RETURNS varchar AS $body$
DECLARE


ds_return_w		varchar(100) := null;
ds_sql			varchar(1000);


BEGIN

if (ie_algorithm_type_p = 'AA') then

	ds_sql :=	'select		nr_pontuacao ' ||
				'from		(select		a.nr_pontuacao ' ||
				'			from		automated_acuity_component	a, ' ||
				'						automated_acuity			b ' ||
				'			where		a.cd_automated_acuity = :cd_sequencia_algorithm_p ' ||
				'			and			a.cd_automated_acuity = b.nr_sequencia ' ||
				'			and			a.ie_sistema_organismo = :ie_sistema_organismo_p ' ||
				'			and			a.dt_observacao is not null ' ||
				'			order by	a.dt_observacao desc, a.nr_sequencia desc) ' ||
				'where		rownum = 1';

else

	ds_sql :=	'select		nr_pontuacao ' ||
				'from		(select		a.nr_pontuacao ' ||
				'			from		early_warning_component		a, ' ||
				'						early_warning				b ' ||
				'			where		a.cd_early_warning = :cd_sequencia_algorithm_p ' ||
				'			and			a.cd_early_warning = b.nr_sequencia ' ||
				'			and			a.ie_componente = :ie_sistema_organismo_p ' ||
				'			and			a.dt_observacao is not null ' ||
				'			order by	a.dt_observacao desc, a.nr_sequencia desc) ' ||
				'where		rownum = 1';

end if;

if (cd_sequencia_algorithm_p IS NOT NULL AND cd_sequencia_algorithm_p::text <> '' AND ie_sistema_organismo_p IS NOT NULL AND ie_sistema_organismo_p::text <> '') then

	begin
		EXECUTE
			ds_sql
		into STRICT
			ds_return_w
		using
			cd_sequencia_algorithm_p, ie_sistema_organismo_p;

	EXCEPTION WHEN no_data_found then
		ds_return_w := '--';

	end;

	if ((ds_return_w = to_char(0)) or (coalesce(ds_return_w::text, '') = '')) then

		ds_return_w := '--';

	end if;

end if;

return ds_return_w;

end;
$body$
