CREATE OR REPLACE FUNCTION get_sequential_breadcrumb () RETURNS varchar AS $body$
DECLARE
	
	ie_atend_breadcrumb_w parametro_atendimento.ie_atend_breadcrumb%type :='N';

BEGIN
		
	SELECT coalesce(MAX(ie_atend_breadcrumb), 'N') ie_atend_breadcrumb
	  INTO STRICT ie_atend_breadcrumb_w
	  FROM 	parametro_atendimento
	 WHERE CD_ESTABELECIMENTO = obter_estabelecimento_ativo;
	
	if (ie_atend_breadcrumb_w = 'P') then
		ie_atend_breadcrumb_w := obter_param_usuario(916, 1247, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_atend_breadcrumb_w);		
	end if;
	
	return ie_atend_breadcrumb_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
