CREATE OR REPLACE FUNCTION get_seq_language_establishment (cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_locale_w	establishment_locale.ds_locale%type;
retorno_w	tasy_idioma.nr_sequencia%type;

BEGIN

select	coalesce(get_establishment_locale(cd_estabelecimento_p),'pt_BR')
into STRICT	ds_locale_w
;

select	coalesce(max(nr_sequencia),'1')
into STRICT 	retorno_w
from	tasy_idioma
where	ds_language_tag = ds_locale_w;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
