CREATE OR REPLACE FUNCTION get_service_aut_rule (cd_convenio_p bigint, cd_estabelecimento_p bigint, ie_type_message_p text) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		message_type_autorization.nr_sequencia%type;


BEGIN
select 	min(nr_sequencia)
into STRICT 	nr_sequencia_w
from 	MESSAGE_TYPE_AUTORIZATION
where 	ie_situacao 		= 'A'
and 	ie_type_message  	= ie_type_message_p
and 	nr_seq_insur_aut 	= (SELECT 	min(nr_sequencia)
								from 	INSURANCE_AUT_SERVICE
								where 	cd_convenio 		= cd_convenio_p
								and 	cd_estabelecimento 	= cd_estabelecimento_p
								and 	ie_situacao = 'A');

return	nr_sequencia_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
