CREATE OR REPLACE FUNCTION get_short_name_department (nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE


ds_short_name_w departamento_medico.ds_short_name%type;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select obter_nome_departamento_medico(max(a.cd_departamento), 'S')
	into STRICT 	ds_short_name_w
	from	atend_paciente_unidade a
	where	a.nr_atendimento = nr_atendimento_p
	and 	a.nr_sequencia = (SELECT max(nr_sequencia)
							  from atend_paciente_unidade 
							  where nr_atendimento = nr_atendimento_p);

end if;

return	ds_short_name_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
