CREATE OR REPLACE FUNCTION get_signature_date (nr_seq_main_daily_rep_p bigint, cd_signature_p bigint) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w			agreg_signatures.dt_signature_1%type := null;
dt_signature_1_w		agreg_signatures.dt_signature_1%type;
dt_signature_2_w		agreg_signatures.dt_signature_2%type;
dt_signature_3_w		agreg_signatures.dt_signature_3%type;
dt_signature_4_w		agreg_signatures.dt_signature_4%type;
dt_signature_5_w		agreg_signatures.dt_signature_5%type;


BEGIN
	
	select dt_signature_1,
		   dt_signature_2,
		   dt_signature_3,
		   dt_signature_4,
		   dt_signature_5
	into STRICT   dt_signature_1_w,
		   dt_signature_2_w,
		   dt_signature_3_w,
		   dt_signature_4_w,
		   dt_signature_5_w
	from   agreg_signatures
	where  nr_seq_main_daily_rep = nr_seq_main_daily_rep_p;
	
	if (cd_signature_p = 1) then
		ds_retorno_w := dt_signature_1_w;
	elsif (cd_signature_p = 2) then
		ds_retorno_w := dt_signature_2_w;
	elsif (cd_signature_p = 3) then
		ds_retorno_w := dt_signature_3_w;
	elsif (cd_signature_p = 4) then
		ds_retorno_w := dt_signature_4_w;
	elsif (cd_signature_p = 5) then
		ds_retorno_w := dt_signature_5_w;
	end if;
	
return ds_retorno_w;
end;
