CREATE OR REPLACE FUNCTION get_signature_in_batch_enabled (cd_perfil_p perfil.cd_perfil%type, nr_seq_projeto_p tasy_projeto_assinatura.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE

	
	ds_retorno_w			varchar(1) := 'N';
	cd_perfil_w		 		perfil.cd_perfil%type;

	c01 CURSOR FOR
		SELECT 	a.ie_permite_lote, a.cd_perfil
		from 	tasy_proj_assin_perfil a
		where 	a.nr_seq_proj = nr_seq_projeto_p;
		

BEGIN

	cd_perfil_w := coalesce(obter_perfil_ativo(), coalesce(cd_perfil_p, 0));
	
	if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') then
	
		<<search_loop01>>
		for r_c01_w in C01
		loop

			if (r_c01_w.ie_permite_lote = 'S') and (coalesce(r_c01_w.cd_perfil, 0) = cd_perfil_w) then
				ds_retorno_w := 'S';
				exit;
			elsif (r_c01_w.ie_permite_lote = 'N') and (coalesce(r_c01_w.cd_perfil, 0) = cd_perfil_w) then
				ds_retorno_w := 'N';
				exit;
			else
				ds_retorno_w := 'T';
			end if;
			
		end loop search_loop01;

		if (ds_retorno_w = 'T') then
		
			<<search_loop02>>
			for r_c01_w in C01
			loop

				if (r_c01_w.ie_permite_lote = 'S') and (coalesce(r_c01_w.cd_perfil, 0) = 0) then
					ds_retorno_w := 'S';
					exit;
				else
					ds_retorno_w := 'N';
				end if;
			
			end loop search_loop02;
		
		end if;

	end if;

	return ds_retorno_w;

end;
$body$
