CREATE OR REPLACE FUNCTION get_sist_corp_escala_vl_alg ( nr_escala_doc_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


ds_return_w				integer;
ds_escala_tabela_w		varchar(255);
ds_escala_atributo_w	varchar(255);
ds_order_atributo_w	varchar(255);
ds_sql					varchar(1000);


BEGIN

if (nr_escala_doc_p IS NOT NULL AND nr_escala_doc_p::text <> '') then

	select		pep.nm_tabela,
				pep.nm_atributo_inf,
        pep.nm_atributo_data
	into STRICT		ds_escala_tabela_w,
				ds_escala_atributo_w,
        ds_order_atributo_w
	FROM		escala_documentacao ed,
				pep_informacao pep
	WHERE		ed.ie_escala = pep.ie_escala
	AND			ed.nr_sequencia = nr_escala_doc_p
	AND			(pep.nm_atributo_inf IS NOT NULL AND pep.nm_atributo_inf::text <> '')
	order by	pep.nm_atributo_inf;

	ds_sql :=	' select ' || ds_escala_atributo_w || ' from ' || ds_escala_tabela_w ||
				' where nr_atendimento = :nr_atendimento_p ' ||
				' and rownum = 1 ' ||
				' order by ' || ds_order_atributo_w;

	begin

		EXECUTE
			ds_sql
		into STRICT
			ds_return_w
		using
			nr_atendimento_p;

	EXCEPTION WHEN no_data_found then

		ds_return_w	    := null;

	end;

end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
