SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_sl_format_type (nr_seq_template_p bigint, nr_seq_strc_father_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w		    varchar(10);
ie_type_w		    varchar(1);
ie_file_format_w	varchar(1);

BEGIN

begin
select  max(ie_type)
into STRICT	ie_type_w
from 	sl_template_structure
where	nr_sequencia = nr_seq_strc_father_p;
exception
when no_data_found  then ie_type_w	:= null;
when others         then  ie_type_w	:= null;
end;

begin
select	max(ie_formato_arquivo)
into STRICT	ie_file_format_w
from	sl_template
where	nr_sequencia = nr_seq_template_p;
exception
when no_data_found  then  ie_file_format_w	:= null;
when others         then  ie_file_format_w	:= null;
end;

if (ie_file_format_w = 'C') then
	if (coalesce(nr_seq_strc_father_p::text, '') = '') then
		ds_return_w := 'R';
	elsif (ie_type_w = 'R') then
		ds_return_w := 'P';
	elsif (ie_type_w = 'P') then
		ds_return_w := 'E';
	end if;
elsif (ie_file_format_w in ('J','T')) then
	if (coalesce(nr_seq_strc_father_p::text, '') = '') then
		ds_return_w := 'G,E';
	elsif (ie_type_w = 'G') then
		ds_return_w := 'G,E';
	end if;
elsif (ie_file_format_w = 'X') then
	if (coalesce(nr_seq_strc_father_p::text, '') = '') then
		ds_return_w := 'G,E';
	elsif (ie_type_w = 'G') then
		ds_return_w := 'G,E,A';
	elsif (ie_type_w = 'E') then
		ds_return_w := 'A';
	end if;
end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
