SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_status_ack_submission ( nr_seq_cpoe_p bigint, ie_tipo_item_p text, ie_suspensao_p text default null) RETURNS varchar AS $body$
DECLARE


ie_qtd_w    		bigint := 0;
ie_return_w 		varchar(1) := 'X';
ie_tipo_item_cpoe_w	varchar(1) := null;


BEGIN

ie_tipo_item_cpoe_w := get_item_type_cpoe_ack(ie_tipo_item_p);

if (ie_tipo_item_cpoe_w IS NOT NULL AND ie_tipo_item_cpoe_w::text <> '') then
	
	select count(nr_seq_cpoe)
	into STRICT ie_qtd_w
	from cpoe_acknowledge_v
	where nr_seq_cpoe = nr_seq_cpoe_p
	and ie_tipo_item_cpoe = ie_tipo_item_cpoe_w;

	if (ie_qtd_w > 0) then
		
		select count(nr_seq_cpoe)
		into STRICT ie_qtd_w
		from cpoe_acknowledge_v
		where nr_seq_cpoe = nr_seq_cpoe_p
		and ie_tipo_item_cpoe = ie_tipo_item_cpoe_w
		and (((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
			and (dt_ack_nurse IS NOT NULL AND dt_ack_nurse::text <> '')
			and (nm_user_ack IS NOT NULL AND nm_user_ack::text <> '')
			and coalesce(ie_suspensao_p, 'N') = 'N')
		or ((dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			and (dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
			and (dt_ack_nurse_susp IS NOT NULL AND dt_ack_nurse_susp::text <> '')
			and (nm_user_ack_susp IS NOT NULL AND nm_user_ack_susp::text <> '')
			and coalesce(ie_suspensao_p, 'S') = 'S'));
		
		if ie_qtd_w > 0 then
			ie_return_w := 'S';
		else
			ie_return_w := 'N';
		end if;
	end if;
end if;

return ie_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
