CREATE OR REPLACE FUNCTION get_status_pc_outpatient_list ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_atend_medico_w		atendimento_paciente.dt_atend_medico%type;
dt_fim_consulta_w		atendimento_paciente.dt_fim_consulta%type;
ds_result_w			valor_dominio.vl_dominio%type := 'A';

/*A - Waiting
O - In consultation
E - Performed*/
BEGIN

select	dt_atend_medico,
	dt_fim_consulta
into STRICT	dt_atend_medico_w,
	dt_fim_consulta_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_p;

if (dt_fim_consulta_w IS NOT NULL AND dt_fim_consulta_w::text <> '') then
	ds_result_w := 'E';
elsif (dt_atend_medico_w IS NOT NULL AND dt_atend_medico_w::text <> '') and (coalesce(dt_fim_consulta_w::text, '') = '') then
	ds_result_w := 'O';
elsif (coalesce(dt_atend_medico_w::text, '') = '') then
	ds_result_w := 'A';
end if;

return	ds_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
