CREATE OR REPLACE FUNCTION get_status_video_notif (CD_VSN_P bigint, NM_USUARIO_P text) RETURNS bigint AS $body$
DECLARE

  DT_VIDEO_ASSESSMENT_V timestamp;
  DT_READ_V TIMESTAMP;
  IE_RETORNO bigint;

BEGIN
  SELECT VSN.DT_VIDEO_ASSESSMENT, VNU.DT_READ INTO STRICT DT_VIDEO_ASSESSMENT_V, DT_READ_V 
  FROM VIDEO_SERVER_NOTIFICATION VSN 
  LEFT JOIN VIDEO_NOTIFICATION_USER VNU ON (VSN.NR_SEQUENCIA = VNU.NR_SEQ_VIDEO_SERVER_NOT AND VNU.NM_USUARIO = NM_USUARIO_P)  
  WHERE VSN.NR_SEQUENCIA = CD_VSN_P;

  IF (DT_VIDEO_ASSESSMENT_V IS NOT NULL AND DT_VIDEO_ASSESSMENT_V::text <> '') THEN 
    IE_RETORNO := 1;
  ELSIF (DT_READ_V IS NOT NULL AND DT_READ_V::text <> '') THEN
    IE_RETORNO := 2;
  ELSE
    IE_RETORNO := 3;
  END IF;
  RETURN IE_RETORNO;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
