CREATE OR REPLACE FUNCTION get_sub_groupo_material ( cd_material_p bigint, ie_structure_p text) RETURNS varchar AS $body$
DECLARE


/* 	ie_estrutura_p
	C = Classe do material
	S = SubGrupo do material
	G = Grupo do material
*/
cd_classe_w		imp_material.cd_classe_material%type;
cd_subgrupo_w	imp_classe_material.cd_subgrupo_material%type;
cd_grupo_w		smallint;
cont_w			smallint;
ds_resultado_w		varchar(100);
ds_subgrup_w    	varchar(50);



BEGIN

begin
select	1
into STRICT	cont_w
from	imp_material
where	cd_material	= cd_material_p;

select	cd_classe_material
into STRICT	cd_classe_w
from	imp_material
where	cd_material = cd_material_p;

if (ie_structure_p = 'C') then
	ds_resultado_w	:= cd_classe_w;
elsif (ie_structure_p = 'S') then
	begin
	select	cd_subgrupo_material
	into STRICT	cd_subgrupo_w
	from	imp_classe_material
	where	cd_classe_material = cd_classe_w;

	select	ds_subgrupo_material
	into STRICT 	ds_subgrup_w
	from 	imp_subgrupo_material
	where 	cd_subgrupo_material = cd_subgrupo_w;
	ds_resultado_w	:= ds_subgrup_w;
	end;
elsif (ie_structure_p = 'G') then
	begin
	select	cd_subgrupo_material
	into STRICT	cd_subgrupo_w
	from	imp_classe_material
	where	cd_classe_material = cd_classe_w;

	select	cd_grupo_material
	into STRICT	cd_grupo_w
	from	imp_subgrupo_material
	where	cd_subgrupo_material = cd_subgrupo_w;

	ds_resultado_w	:= cd_grupo_w;
	end;
end if;
exception
when others then

	ds_resultado_w := null;
end;

RETURN ds_resultado_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
