CREATE OR REPLACE FUNCTION get_surgery_data ( dt_agenda timestamp, dt_cutoff_time text) RETURNS varchar AS $body$
BEGIN
  IF (TRUNC(dt_agenda - clock_timestamp()) < 0) THEN
    RETURN 'S';
  ELSIF(TRUNC(dt_agenda - (clock_timestamp() - interval '1 days')) > -1 AND TRUNC(dt_agenda - (clock_timestamp() - interval '1 days')) <= 1) THEN
    IF (TRUNC(dt_agenda - (clock_timestamp() - interval '1 days')) = 1 AND (to_date(dt_cutoff_time,'HH24:MI') - to_date(TO_CHAR(clock_timestamp(),'HH24:MI'),'HH24:MI')) >=0) THEN
      RETURN 'N';
    ELSE
      RETURN 'S';
    END IF;
   ELSIF (TRUNC(dt_agenda - (clock_timestamp() - interval '1 days')) > 1) THEN
      RETURN 'N';
  END IF;
RETURN 'S';
END;
$body$
