CREATE OR REPLACE FUNCTION get_sur_sche_permission_list ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, ds_agendas_p text) RETURNS varchar AS $body$
DECLARE


ds_lista_agendas_w 	varchar(4000) := '';
ds_schedule_rule_w	varchar(1);
cd_perfil_w			perfil.cd_perfil%type;WITH RECURSIVE cte AS (


schedule_sequencies CURSOR FOR
SELECT level as id, (regexp_substr(ds_agendas_p, '[^,]+', 1, level))::numeric  as data

(regexp_substr(ds_agendas_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(ds_agendas_p, '[^,]+', 1, level))::text <> '')  UNION ALL


schedule_sequencies CURSOR FOR
SELECT level as id, (regexp_substr(ds_agendas_p, '[^,]+', 1, level))::numeric  as data

(regexp_substr(ds_agendas_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(ds_agendas_p, '[^,]+', 1, level))::text <> '') JOIN cte c ON ()

) SELECT * FROM cte;
;
						
BEGIN

	cd_perfil_w := wheb_usuario_pck.get_cd_perfil;

	for schedule_sequece in schedule_sequencies loop
		ds_schedule_rule_w := obter_se_perm_agecirur(null, cd_pessoa_fisica_p, cd_perfil_w, schedule_sequece.data);
		ds_lista_agendas_w := ds_lista_agendas_w || schedule_sequece.data || '-' || ds_schedule_rule_w || ';';
	end loop;

	return ds_lista_agendas_w;

end;
$body$
