CREATE OR REPLACE FUNCTION get_tot_serv_item ( nr_seq_pacote_p ptu_pacote_reg.nr_sequencia%type, ie_tipo_item_p ptu_pacote_servico.ie_tipo_item%type default null) RETURNS bigint AS $body$
DECLARE


/*+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
ie_tipo_item_p

          vl_tot_tax_gas -> ie_tipo_item_p = 1
          vl_tot_diarias -> ie_tipo_item_p = 2
          vl_tot_mat     -> ie_tipo_item_p = 4
          vl_tot_med     -> ie_tipo_item_p = 5
          vl_tot_proc    -> ie_tipo_item_p = 6
          vl_tot_opme    -> ie_tipo_item_p = 7

ie_tipo_item_p = null (Retorna o total geral de todos itens)
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/vl_retorno_w  double precision :=0;


BEGIN	
		
	begin
		select	sum(ser.vl_servico)
		into STRICT 	vl_retorno_w
		from 	ptu_pacote_reg reg,
			ptu_pacote_servico ser
		where 	reg.nr_sequencia = ser.nr_seq_pacote_reg
		and 	reg.nr_sequencia = nr_seq_pacote_p
		and (ser.ie_tipo_item = ie_tipo_item_p or
		coalesce(ie_tipo_item_p,0) = 0);
		
	exception
	when no_data_found then
		vl_retorno_w := 0;
	end;
	
return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
