CREATE OR REPLACE FUNCTION get_url_ged ( cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE




ds_url_w		varchar(255);



BEGIN


select	max(coalesce(upper(ds_url_ged),''))
into STRICT	ds_url_w
from	parametro_medico
where	cd_estabelecimento = cd_estabelecimento_p;


if (ds_url_w IS NOT NULL AND ds_url_w::text <> '') then
	begin

	ds_url_w	:=	 replace(replace(ds_url_w,'$CD_PESSOA_FISICA',cd_pessoa_fisica_p),'$NM_USUARIO',nm_usuario_p);

	end;
end if;

return	ds_url_w;

