CREATE OR REPLACE FUNCTION get_user_calendar ( nm_usuario_p text, cd_estabelecimento_p bigint default null, ie_expected_result_p text default 'C') RETURNS varchar AS $body$
DECLARE


/*ie_expected_result_p accepted values:
P - Primary calendar
S - Secondary calendar
C - Combined calendar identifier, separated by '/'*/
ds_calendar_w		varchar(255);
ds_secondary_calendar_w	calendar.ds_calendar%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;


BEGIN
select	max(ds_calendar),
		max(ds_secondary_calendar)
into STRICT	ds_calendar_w,
		ds_secondary_calendar_w
from	user_locale
where	nm_user = nm_usuario_p;

if (coalesce(ds_calendar_w::text, '') = '') then
	if (coalesce(cd_estabelecimento_p::text, '') = '') then
		select	max(coalesce(wheb_usuario_pck.get_cd_estabelecimento,cd_estabelecimento))
		into STRICT	cd_estabelecimento_w
		from	usuario
		where	nm_usuario = nm_usuario_p;
	end if;

	select	coalesce(max(ds_calendar),'iso8601'),
			max(ds_secondary_calendar)
	into STRICT	ds_calendar_w,
			ds_secondary_calendar_w
	from	establishment_locale
	where	cd_estabelecimento = coalesce(cd_estabelecimento_p,cd_estabelecimento_w);
end if;

if (coalesce(ie_expected_result_p,'C') = 'C') and (ds_secondary_calendar_w IS NOT NULL AND ds_secondary_calendar_w::text <> '') then
	ds_calendar_w := ds_calendar_w || '/' || ds_secondary_calendar_w;
elsif (coalesce(ie_expected_result_p,'C') = 'S') then
	ds_calendar_w := ds_secondary_calendar_w;
end if;

return ds_calendar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
