SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_uses_dpc () RETURNS varchar AS $body$
DECLARE


    w_ie_dpc    varchar(1) := 'N';


BEGIN
    select   coalesce(max(ie_dpc), 'N')
    into STRICT     w_ie_dpc
    from     parametros_episodio
    where    cd_perfil = wheb_usuario_pck.get_cd_perfil
    and      cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
    order by 1;

    return w_ie_dpc;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
