SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_ventilation_hours (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

  /* 
   
   
   
  Tipo de gasoterapia: 
   
   
   
    INV = Invasive 
   
   
   
    NIV = Non Invasive 
   
   
   
  */
 
  ie_evento_w  varchar(15);
  ie_somar_w   varchar(1);
  qt_minutos_w bigint := 0;
  dt_evento_w  timestamp;
  dt_inicio_w  timestamp;
  c01 CURSOR FOR
    SELECT dt_evento, 
           ie_evento 
    FROM   prescr_gasoterapia_evento a, 
           prescr_gasoterapia b, 
           prescr_medica c 
    WHERE  b.nr_sequencia = a.nr_seq_gasoterapia 
           AND b.nr_prescricao = c.nr_prescricao 
           AND c.nr_atendimento = nr_atendimento_p 
           --and dt_prescricao between trunc(dt_inicial_p) and fim_dia(dt_final_p) 
           AND coalesce(a.ie_evento_valido, 'S') = 'S' 
           AND (b.ie_respiracao IN ( 'ESPONT', 'VMI', 'VMIFB', 'BORB', 'VMNI' 
                                     )) 
    ORDER  BY dt_evento;

BEGIN
    IF (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') THEN 
      OPEN c01;

      LOOP 
          FETCH c01 INTO dt_evento_w, ie_evento_w;

          EXIT WHEN NOT FOUND; /* apply on c01 */

          IF ( ie_evento_w IN ( 'R', 'I' ) ) THEN 
            dt_inicio_w := dt_evento_w;

            ie_somar_w := 'S';
          ELSIF ( ie_evento_w IN ( 'IN', 'T', 'TE' ) ) 
                AND ( ie_somar_w = 'S' ) THEN 
            qt_minutos_w := qt_minutos_w 
                            + Obter_min_entre_datas(dt_inicio_w, dt_evento_w, 1) 
;

            ie_somar_w := 'N';
          END IF;
      END LOOP;

      CLOSE c01;
    END IF;

    RETURN qt_minutos_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION get_ventilation_hours (nr_atendimento_p bigint) FROM PUBLIC;

