CREATE OR REPLACE FUNCTION get_vital_sign_weight_change ( nr_seq_atend_sinal_vital_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finality:
---------------------------------------------------------------------------------------------------
Direct calls:
[ X ]  Object dictionary [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Reports [ ] Others:
---------------------------------------------------------------------------------------------------
Attention points:
    ie_opcao_p
        P - Compares with the first registration
        U - Compares with the last one registration
---------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w					double precision;
cd_pessoa_fisica_w          	atendimento_sinal_vital.cd_pessoa_fisica%type;
nr_seq_atend_sinal_vital_w  	atendimento_sinal_vital.nr_sequencia%type;
qt_peso_ini_w               	atendimento_sinal_vital.qt_peso%type := 0;
qt_peso_fim_w               	atendimento_sinal_vital.qt_peso%type := 0;
dt_sinal_vital_w            	atendimento_sinal_vital.dt_sinal_vital%type;
nr_atendimento_w				atendimento_paciente.nr_atendimento%type;
qt_peso_w						atendimento_sinal_vital.qt_peso%type;

C01 CURSOR FOR
	SELECT * from (
		SELECT  nr_sequencia,
				qt_peso
		from    atendimento_sinal_vital
		where   cd_pessoa_fisica = cd_pessoa_fisica_w
		and		nr_atendimento = nr_atendimento_w
		and		(qt_peso IS NOT NULL AND qt_peso::text <> '')
		order by dt_sinal_vital desc) alias1 LIMIT 2;


BEGIN
select  max(a.cd_pessoa_fisica),
		max(a.nr_atendimento)
into STRICT    cd_pessoa_fisica_w,
		nr_atendimento_w
from    atendimento_sinal_vital a
where   a.nr_sequencia  = nr_seq_atend_sinal_vital_p;

if (ie_opcao_p = 'P') then
	ds_retorno_w := '0';
elsif (ie_opcao_p = 'U') then

	open C01;
	loop
	fetch C01 into
		nr_seq_atend_sinal_vital_w,
		qt_peso_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		if (qt_peso_fim_w = 0) then
			qt_peso_fim_w := qt_peso_w;
		else
			qt_peso_ini_w := qt_peso_w;
		end if;
	end loop;
	close C01;

	if (qt_peso_ini_w > 0) then
		ds_retorno_w := round(((qt_peso_fim_w * 100) / qt_peso_ini_w) - 100);
	else
		ds_retorno_w := '0';
	end if;

end if;

return ds_retorno_w;

end;
$body$
