CREATE OR REPLACE FUNCTION get_vl_bordero_pag_por_titulo (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


vl_bordero_novo_w	bordero_tit_pagar.vl_bordero%type;
vl_bordero_antigo_w	titulo_pagar.vl_bordero%type;


BEGIN

--Quando parametro 12 for S, vai pegar da bordero_tit_pagar
select	sum(coalesce(vl_bordero, 0) - coalesce(vl_desconto_bordero, 0) + coalesce(vl_juros_bordero, 0) + coalesce(vl_multa_bordero, 0) + coalesce(vl_out_desp_bordero, 0))
into STRICT	vl_bordero_novo_w
from	bordero_tit_pagar
where	nr_titulo	= nr_titulo_p;

--Quando parametro 12 for N, vai pegar do proprio titulo a pagar em titulo_pagar_v
select	sum(coalesce(vl_bordero, 0) - coalesce(vl_desconto_bordero, 0) + coalesce(vl_juros_bordero, 0) + coalesce(vl_multa_bordero, 0) + coalesce(vl_out_desp_bordero, 0))
into STRICT	vl_bordero_antigo_w
from	titulo_pagar_v
where	nr_titulo	= nr_titulo_p;

return	coalesce(vl_bordero_novo_w,0) + coalesce(vl_bordero_antigo_w,0);

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
