CREATE OR REPLACE FUNCTION get_week_day_gest (nr_atendimento_p bigint, data_gestacao_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w					varchar(255) 	:= null;
dt_ultima_menstruacao_w 		timestamp 			:= data_gestacao_p;
qt_sem_decimais_w 				bigint;
qt_days_w						bigint;
qt_weeks_w						bigint;
get_dias_gest_w					bigint;


BEGIN
	SELECT 		trunc(clock_timestamp()) - trunc(dt_ultima_menstruacao_w)
	INTO STRICT		get_dias_gest_w
	;
	
	IF ((dt_ultima_menstruacao_w IS NOT NULL AND dt_ultima_menstruacao_w::text <> '') AND get_dias_gest_w <= 294) THEN
		
		SELECT	ROUND((trunc(clock_timestamp()) - trunc(dt_ultima_menstruacao_w))/7,8) qt_week
		INTO STRICT 		qt_sem_decimais_w
		;
		
		SELECT ROUND	((qt_sem_decimais_w - trunc(qt_sem_decimais_w)) * 7)
		INTO STRICT 			qt_days_w
		;
		
		SELECT
		REPLACE(SUBSTR(qt_sem_decimais_w, 1, 2), ',', '')
		INTO STRICT 		qt_weeks_w
		;
		
		ds_retorno_w := qt_weeks_w ||' Semanas e ' || qt_days_w || ' Dias.';
	END IF;
	
	RETURN ds_retorno_w;
	
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
