CREATE OR REPLACE FUNCTION get_weight_date_lvs (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

    result_w varchar(80);

BEGIN
    select CASE WHEN ie_unid_med_peso='Kg' THEN  qt_peso  ELSE qt_peso_atual END  || ie_unid_med_peso || ' - ' || PKG_DATE_FORMATERS.TO_VARCHAR(DT_SINAL_VITAL, 'timestamp', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, WHEB_USUARIO_PCK.GET_NM_USUARIO)
	into STRICT   result_w	
    from   atendimento_sinal_vital	
    where  nr_sequencia = (SELECT coalesce(max(nr_sequencia), 0)
                from  atendimento_sinal_vital
                where CASE WHEN ie_unid_med_peso='Kg' THEN  qt_peso  ELSE qt_peso_atual (END IS NOT NULL AND END::text <> '')
                and   coalesce(ie_situacao, 'A') = 'A'
                and   cd_paciente = cd_pessoa_fisica_p);
    RETURN result_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
