CREATE OR REPLACE FUNCTION gltn_obter_exporta_xml_posto (nr_seq_posto_p bigint) RETURNS varchar AS $body$
DECLARE

ie_exporta_xml_w	varchar(1) := 'N';

BEGIN
 
if (nr_seq_posto_p IS NOT NULL AND nr_seq_posto_p::text <> '') then 
 
	select	coalesce(Max(a.ie_exportar_xml), 'N') ie_exportar_xml 
	into STRICT	ie_exporta_xml_w 
	from	lote_ent_inst_posto a 
	where	a.nr_sequencia = nr_seq_posto_p;
	 
end if;
 
return	ie_exporta_xml_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
