CREATE OR REPLACE FUNCTION gnp_obter_pr_desc_procmat ( nr_seq_item_p bigint, ie_tipo_item_p bigint) RETURNS bigint AS $body$
DECLARE

 
pr_valor_w	bigint;


BEGIN 
 
if (ie_tipo_item_p = 1) then 
 
	select	coalesce(max(pr_valor),0) 
	into STRICT	pr_valor_w 
	from	proc_paciente_valor 
	where	ie_tipo_valor		= 3 
	and	nr_seq_procedimento	= nr_seq_item_p;
 
elsif (ie_tipo_item_p = 2) then 
 
	select	coalesce(max(a.pr_valor),0) 
	into STRICT	pr_valor_w 
	from	mat_atend_paciente_valor a 
	where	a.nr_seq_material		= nr_seq_item_p 
	and	a.ie_tipo_valor		= 3;
	 
end if;
 
 
return	pr_valor_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
