CREATE OR REPLACE FUNCTION goq_obter_protocolo_medicacao (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(500);
ds_protocolo_w varchar(500);
ds_medic_w varchar(500);
exib_prot_medic_w varchar(1);


BEGIN
  if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then
      exib_prot_medic_w := Obter_param_usuario(380, 12, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, exib_prot_medic_w);
    select obter_desc_protocolo(cd_protocolo),
           obter_desc_protocolo_medic(nr_seq_medicacao, cd_protocolo)
    into STRICT ds_protocolo_w,
         ds_medic_w
    from paciente_setor
    where nr_seq_paciente = nr_seq_paciente_p;

    if (exib_prot_medic_w = 'C') then
        if (ds_protocolo_w IS NOT NULL AND ds_protocolo_w::text <> '') or (ds_medic_w IS NOT NULL AND ds_medic_w::text <> '') then
        ds_retorno_w := ds_protocolo_w || ' / ' || ds_medic_w;
        end if;
    elsif (exib_prot_medic_w = 'P') then
        ds_retorno_w := ds_protocolo_w;
    elsif (exib_prot_medic_w = 'M') then
        ds_retorno_w := ds_medic_w;
    else
        ds_retorno_w := '***** / *****';
    end if;
  end if;
  return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
