CREATE OR REPLACE FUNCTION gpi_mostra_grupo_trab ( nr_seq_estrutura_p bigint, nr_seq_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mostra_w	varchar(1);
nr_seq_superior_w	bigint;
qt_registros_w	bigint;


BEGIN

ie_mostra_w := 'N';

select	count(*)
into STRICT	qt_registros_w
from	gpi_estrutura_grupo g
where	g.nr_seq_estrutura = nr_seq_estrutura_p
and	g.nr_seq_grupo = nr_seq_grupo_p;

if (qt_registros_w > 0) then
	ie_mostra_w := 'S';
end if;

if (ie_mostra_w = 'N') then

	select	coalesce(max(nr_seq_superior),0)
	into STRICT	nr_seq_superior_w
	from	gpi_estrutura e
	where	e.nr_sequencia = nr_seq_estrutura_p;

	if (nr_seq_superior_w <> 0) then
		select	gpi_mostra_grupo_trab(nr_seq_superior_w, nr_seq_grupo_p)
		into STRICT	ie_mostra_w
		;
	else
		ie_mostra_w := 'N';
	end if;

end if;

return	ie_mostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
