SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpi_obter_acomp_orcamento ( ie_tipo_valor_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


vl_retorno_w	w_gpi_acomp_orcamento.vl_orcado%type;				
				
/*
ie_tipo_valor_p

R - Valor realizado
O - Valor orcado
*/
				
				

BEGIN
if (ie_tipo_valor_p IS NOT NULL AND ie_tipo_valor_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	
	if (ie_tipo_valor_p = 'O') then
		select sum(vl_orcado)
		into STRICT vl_retorno_w
		from (  SELECT max(a.vl_orcado)
                as vl_orcado
                from		w_gpi_acomp_orcamento a
                where		a.nm_usuario	= nm_usuario_p
                and 		gpi_obter_nivel_etapa(nr_seq_etapa) = 1
                group by 	nr_seq_etapa) alias6
		where 1 = 1;
	else
		select sum(vl_realizado)
		into STRICT vl_retorno_w
		from (
			SELECT max(a.vl_realizado)
			as vl_realizado
			from		w_gpi_acomp_orcamento a
			where		a.nm_usuario	= nm_usuario_p
			and 		gpi_obter_nivel_etapa(nr_seq_etapa) = 1
			group by 	nr_seq_etapa) alias3
		where 1 = 1;
	end if;

end if;
return	vl_retorno_w;

end;	
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
