CREATE OR REPLACE FUNCTION gpi_obter_apres_etapa ( cd_classificacao_p text, nm_etapa_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_espaco_w	varchar(255);
BEGIN


if (coalesce(cd_classificacao_p, 'X') <> 'X') then
	begin


	for i in 1..Length(cd_classificacao_p) loop
		begin
		if (substr(cd_classificacao_p, i, 1) = '.') then

			ds_espaco_w	:= substr(ds_espaco_w || '      ', 1, 255);
		end if;
		end;
	end loop;

	ds_retorno_w	:= substr(ds_espaco_w || nm_etapa_p, 1, 255);
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
