SET client_encoding TO 'UTF8';



CREATE TYPE campos AS (	nr_seq_apres		bigint);


CREATE OR REPLACE FUNCTION gpi_obter_classif_estrut ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


type vetor is table of campos index by integer;
estrutura_w			vetor;
i				integer		:= 0;
nr_sequencia_w			bigint	:= nr_sequencia_p;
nr_seq_apres_w			bigint;
nr_seq_superior_w			bigint;
qt_nivel_w			bigint	:= 1;
cd_classificacao_w			varchar(255);


BEGIN
/* Obter a sequencia de apresentação da etapa*/

select	max(nr_seq_superior),
	coalesce(max(nr_sequencia),1)
into STRICT	nr_seq_superior_w,
	nr_seq_apres_w
from	gpi_estrutura
where	nr_sequencia		= nr_sequencia_w;

estrutura_w[i].nr_seq_apres	:= nr_seq_apres_w;

/* Obter as sequencias de apresentação superiores*/

while(nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') loop
	begin
	nr_sequencia_w		:= nr_seq_superior_w;
	i			:= i + 1;

	select	max(nr_seq_superior),
		coalesce(max(nr_sequencia),1)
	into STRICT	nr_seq_superior_w,
		nr_seq_apres_w
	from	gpi_estrutura
	where	nr_sequencia	= nr_sequencia_w;

	estrutura_w[i].nr_seq_apres	:= nr_seq_apres_w;

	end;
end loop;
/* Define a classificação da etapa */

if (estrutura_w.Count = 1) then
	cd_classificacao_w	:= trim(both to_char(estrutura_w[i].nr_seq_apres,'009'));
elsif (estrutura_w.Count > 1) then
	begin
	FOR i IN REVERSE estrutura_w.Count-1..0 loop
		begin
		cd_classificacao_w	:= substr(cd_classificacao_w || trim(both to_char(estrutura_w[i].nr_seq_apres,'009')) || '.',1,255);
		end;
	end loop;
	end;
end if;
/* se o ultimo caracter for um ponto, retirar da variavel */

if (substr(cd_classificacao_w,length(cd_classificacao_w),1) = '.') then
	cd_classificacao_w	:= substr(cd_classificacao_w,1,length(cd_classificacao_w)-1);
end if;

return	cd_classificacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
