CREATE OR REPLACE FUNCTION gpi_obter_conta ( cd_empresa_p bigint, dt_vigencia_p timestamp, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
dt_vigencia_w		timestamp;
nr_seq_conta_gpi_w	bigint;
nr_seq_conta_gpi_ww	bigint;

c01 CURSOR FOR
SELECT	a.nr_seq_conta_gpi
from	gpi_regra_plano a
where	a.cd_empresa					= cd_empresa_p
and	coalesce(a.cd_material,cd_material_p) 			= cd_material_p
and	coalesce(a.cd_grupo_material,cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(a.cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(a.cd_classe_material,cd_classe_material_w)		= cd_classe_material_w
and (coalesce(dt_inicio_vigencia, dt_vigencia_w) <= dt_vigencia_w and coalesce(dt_fim_vigencia, dt_vigencia_w) >= dt_vigencia_w)
order by	a.dt_inicio_vigencia,
		coalesce(a.cd_material,0),
		coalesce(a.cd_classe_material,0),
		coalesce(a.cd_subgrupo_material,0),
		coalesce(a.cd_grupo_material,0);

BEGIN

dt_vigencia_w	:= trunc(dt_vigencia_p,'dd');

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v a
where	a.cd_material	= cd_material_p;

open c01;
loop
fetch c01 into
	nr_seq_conta_gpi_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	nr_seq_conta_gpi_ww	:= nr_seq_conta_gpi_w;
end loop;
close c01;

return nr_seq_conta_gpi_ww;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
