CREATE OR REPLACE FUNCTION gpi_obter_cor_tipo_doc (nr_sequencia_p gpi_tipo_documento.nr_sequencia%type, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cor_fonte_w 			gpi_tipo_documento.ds_cor_fonte%type;
ds_cor_fundo_w 			gpi_tipo_documento.ds_cor_fundo%type;
ds_cor_html_w			gpi_tipo_documento.ds_cor_html%type;

BEGIN

if (coalesce(nr_sequencia_p,0) != 0) and (ie_opcao_p > 0) then
	begin
	select	ds_cor_fonte,
		ds_cor_fundo,
		ds_cor_html
	into STRICT	ds_cor_fonte_w,
		ds_cor_fundo_w,
		ds_cor_html_w
	from	gpi_tipo_documento
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 1) then
		return ds_cor_fonte_w;
	elsif (ie_opcao_p = 2) then
		return ds_cor_fundo_w;
	elsif (ie_opcao_p = 3) then
		return ds_cor_html_w;
	end if;
	end;
end if;

return '';

end;
$body$
LANGUAGE PLPGSQL
